import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import styles from './App.module.css';

type Message = { sender: string; text: string };

const App = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef<HTMLDivElement | null>(null);

  // Authentication states
  const [mode, setMode] = useState<'login' | 'register' | 'chat'>('chat');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMsg = { sender: 'user', text: input };
    setMessages(prev => [...prev, userMsg]);
    setInput('');

    try {
      const res = await axios.post('http://localhost:8000/message', { message: input });
      const aiMsg = { sender: 'ai', text: res.data.reply };
      setMessages(prev => [...prev, aiMsg]);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Dummy login/register handlers (replace with real API calls)
  const handleLogin = () => {
    if (username && password) {
      // fake login success
      setIsAuthenticated(true);
      setMode('chat');
      setUsername('');
      setPassword('');
    }
  };

  const handleRegister = () => {
    if (username && password) {
      // fake register success
      setIsAuthenticated(true);
      setMode('chat');
      setUsername('');
      setPassword('');
    }
  };

  if (!isAuthenticated) {
    return (
      <div className={styles.chatContainer}>
        <div style={{ marginBottom: 20 }}>
          <button onClick={() => setMode('login')} disabled={mode === 'login'}>Login</button>
          <button onClick={() => setMode('register')} disabled={mode === 'register'}>Register</button>
        </div>

        {mode === 'login' && (
          <div>
            <h2>Login</h2>
            <input
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            /><br />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            /><br />
            <button onClick={handleLogin}>Submit</button>
          </div>
        )}

        {mode === 'register' && (
          <div>
            <h2>Register</h2>
            <input
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            /><br />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            /><br />
            <button onClick={handleRegister}>Submit</button>
          </div>
        )}
      </div>
    );
  }

  // Authenticated Chat UI
  return (
    <div className={styles.chatContainer}>
      <div style={{ marginBottom: 20 }}>
        <button onClick={() => setIsAuthenticated(false)}>Logout</button>
      </div>
      <h2>Chat with AI</h2>
      <div className={styles.messages}>
        {messages.map((msg, i) => (
          <div key={i}><b>{msg.sender}:</b> {msg.text}</div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className={styles.inputRow}>
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => {
            if (e.key === 'Enter') sendMessage();
          }}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default App;
